import { Component, HostListener } from '@angular/core';
import { ItemServiceService } from 'src/app/services/item-service.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  bgColor = 'transparent'; // Initial background color
constructor(private itemSer:ItemServiceService){}

  @HostListener('window:scroll', ['$event'])
  onScroll(event: any) {
    if (window.pageYOffset > 50) {
      this.bgColor = '#ffff'; // Change background color after scrolling 50px
    } else {
      this.bgColor = 'transparent'; // Change background color back to transparent
    }
  }

  cartCount:number=0;

  cartData() {
    let user = localStorage.getItem('user');
    let userId = user && JSON.parse(user).data.id;
    this.itemSer.getCartDataByUserID(userId).subscribe((res: any) => {

      this.cartCount = res.data.length;
      console.log(this.cartCount, "CartCount");


    })
  }
}
