import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ItemServiceService } from 'src/app/services/item-service.service';
import { ToasterService } from 'src/app/services/toaster.service';
import { UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit {
  edit: boolean = false;
  bgColor: any = '#f0ad4e';
  updateProfile: FormGroup;


  name: any;
  email: any;
  phone: any;
  password: any;

  user: any = {
    name: '',
    email: '',
    phone: '',
    password: '',
  }
  constructor(private FB: FormBuilder, private US: UsersService, private toaster:ToasterService, private itemSer:ItemServiceService) {
    this.updateProfile = this.FB.group({
      name: this.FB.control('', [Validators.required]),
      email: this.FB.control('', [Validators.required]),
      phone: this.FB.control('', [Validators.required]),
      password: this.FB.control('', [Validators.required]),
    })
  }

  ngOnInit() {
    this.checkUser();
    this.em
  }

  editProfile(FormData: any) {
    this.edit = !this.edit;
    this.user.name = this.name;
    this.user.email = this.email;
    this.user.phone = this.phone;
    this.user.password = this.password;
    if (!this.edit) {
      this.bgColor = '#f0ad4e';
      let user: any = localStorage.getItem("user");
      let UserId = user && JSON.parse(user).data.id;
      this.US.updateProfile(UserId, FormData).subscribe((res: any) => {
        this.toaster.ProfileUpdateSuccess();
        this.checkUser();
      },err=>{
        this.toaster.ProfileUpdateFail();
      })
      // Write Profile Update Code Here

      // 8269730633
    } else {
      this.bgColor = '#28a745';
    }
  }

  checkUser() {
    let user: any = localStorage.getItem("user");
    let UserId = user && JSON.parse(user).data.id;
    if (UserId) {
      this.US.getProfile(UserId).subscribe((res: any) => {
        this.name = res.data.name;
        this.email = res.data.email;
        this.phone = res.data.phone;
        this.password = res.data.password;
      })
    }
  }

  emitUser() {
    let user = localStorage.getItem("user");
    let userVerify = user && JSON.parse(user).data.id
    if(userVerify){
   this.itemSer.ActiveUser.emit(true);
  }
  }

}
<div class="container mt-4 mb-4">
  <div class="row d-flex">
    <div class="col-12 col-sm-6 d-flex">
      <div class="row d-flex">
        <div class="col-12 col-sm-8">

          <h5>Name</h5>
          <p>{{ name }}</p>

          <h5>Email</h5>
          <p>{{ email }}</p>

          <h5>Phone</h5>
          <p>{{ phone }}</p>

          <h5>Password</h5>
          <p>**************</p>

        </div>

        <div class="col-12 col-sm-4">
          <img src="../../../assets/61050397.jpg" alt="Image Not
                        Found" class="img-fluid" />
          <div class="editProfile" style="margin-top: 100px;">
            <button class="btn " (click)="editProfile(updateProfile.value)"
              [ngStyle]="{'background-color': bgColor}">{{ edit? 'Update Profile' : 'Edit Profile' }}</button>
          </div>

        </div>

      </div>
    </div>



    <div class="col-6" *ngIf="edit">
      <form [formGroup]="updateProfile" (ngSubmit)="editProfile(updateProfile.value)">

        <div class="formInput  mb-2">
          <label for="">Name</label>
          <input type="text" formControlName="name" [(ngModel)]="user.name" value="{{ user.name }}"
            class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Phone</label>
          <input type="text" formControlName="phone" [(ngModel)]="user.phone" value="{{ user.phone }}"
            class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Email</label>
          <input type="email" formControlName="email" [(ngModel)]="user.email" value="{{ user.email }}"
            class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Password</label>
          <input type="password" formControlName="password" [(ngModel)]="user.password" value="{{ user.password }}"
            class="form-control">
        </div>


      </form>
    </div>

  </div>

</div>
