import { Injectable } from '@angular/core';
import {  ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root'
})
export class ToasterService {

  constructor(private toaster:ToastrService) { 

  }


  QuantityUpdatedPlus() {
    this.toaster.info('One More Quantity Addded This Item', 'FoodCounter Says!');
  }

  QuantityUpdatedMinus() {
    this.toaster.warning('One Quantity Discrease This Item', 'FoodCounter Says!');
  }

  LoginSuccess() {
    this.toaster.success('Login Success! ', 'FoodCounter Says!');
  }

 RegisterSuccess() {
    this.toaster.success('Registeration Success!, Please Wait You Will Redirect To Login Page.. ', 'FoodCounter Says!');
  }

  AddToCartSuccess() {
    this.toaster.success('Item Added To Cart!', 'FoodCounter Says!');
  }

  AddToCartSuccess() {
    this.toaster.success('Item Added To Cart!', 'FoodCounter Says!');
  }

}
