import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit {
  edit: boolean = false;
  bgColor: any = '#f0ad4e';
  updateProfile : FormGroup;


  name:any;
  email:any;
  phone:any;
  password:any;

  user:any ={
    name: '',
   email: '',
    phone: '',
    password: '',
  }
constructor(private FB:FormBuilder){
this.updateProfile = this.FB.group({
  name : this.FB.control('',[Validators.required]),
  email : this.FB.control('',[Validators.required]),
  phone : this.FB.control('',[Validators.required]),
  password : this.FB.control('',[Validators.required]),
})
}

ngOnInit(){
  this.checkUser();

}

  editProfile(value:any) {

    this.edit = !this.edit;
this.user.name = this.name;
this.user.email = this.email;
this.user.phone = this.phone;
this.user.password = this.password;
    if (!this.edit) {
      this.bgColor = '#f0ad4e';
      console.log("Updated");
      // Write Profile Update Code Here


    } else {
      console.log("Not");
      this.bgColor = '#28a745';
    }

  }

  checkUser(){
    let user:any = localStorage.getItem("user");
    let verifyUser = user && JSON.parse(user);
    if(verifyUser){
      this.name = verifyUser.data.name;
      this.email = verifyUser.data.email;
      this.phone = verifyUser.data.phone;
      this.password = verifyUser.data.password;
    }
  }

}
