import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit {
  edit: boolean = false;
  bgColor: any = '#f0ad4e';

  name:any;
  email:any;
  phone:any;
  password:any;
constructor(){

  this.checkUser();

}

ngOnInit(){
  this.checkUser();

}

  editProfile() {

    this.edit = !this.edit;

    if (!this.edit) {
      this.bgColor = '#f0ad4e';
      console.log("Updated");
      // Write Profile Update Code Here


    } else {
      console.log("Not");
      this.bgColor = '#28a745';
    }

  }

  checkUser(){
    let user:any = localStorage.getItem("user");
    let verifyUser = user && JSON.parse(user);
    if(verifyUser){
      this.name = verifyUser.data.name;
      this.email = verifyUser.data.email;
      this.phone = verifyUser.data.phone;
      this.password = verifyUser.data.password;
    }
  }

}
