import { Component, OnInit } from '@angular/core';
import { ItemServiceService } from 'src/app/services/item-service.service';

@Component({
  selector: 'app-add-to-cart',
  templateUrl: './add-to-cart.component.html',
  styleUrls: ['./add-to-cart.component.scss']
})
export class AddToCartComponent implements OnInit {
cartList:any;
quantity:any;
constructor(private IS:ItemServiceService){}

ngOnInit() {
 this.cartData();
}

plusQuantity(id:any, qty:any){
  if(qty < 9){
    qty++;
    this.IS.updateCartQuantity(id,qty).subscribe((res:any)=>{
      console.log("QTY UPDATED");

    },(err)=>{
console.log("fail");

    })

   }
}

minusQuantity(id:any, qty:any){
  // if(this.quantity > 1){
  // this.quantity--;
  // this.totalPrice = this.price*this.quantity
  // console.log(this.totalPrice);
  // }
}


cartData(){
this.IS.getCartData().subscribe((res:any)=>{
  this.cartList = res;
  console.log(this.cartList,"cart");

})
}

}
